def StartGame(level):
    global secili_liste, secili_keys, secili_values, game_frame, key_labels, value_labels, var2, var3
    
    if level == "A1":
        secili_liste = A1.copy()
    if level == "A2":
        secili_liste = A2.copy()
    if level == "B1":
        secili_liste = B1.copy()
    if level == "B2":
        secili_liste = B2.copy()
    if level == "C1":
        secili_liste = C1.copy()
    if level == "C2":
        secili_liste = C2.copy()

    secili_keys = []
    secili_values = []

    for x in range(5):
        key, value = r.choice(secili_liste)
        # seçilenin silineceği kısım
        secili_keys.append(key)
        secili_values.append(value)
        r.shuffle(secili_keys)
        r.shuffle(secili_values)
        
    var2 = StringVar()
    k1 = Radiobutton(game_frame, text=secili_keys[0], variable=var2, value=secili_keys[0], indicator=0, command=ClickedKey, background="#03A9F4", foreground="#000", activebackground="#03A9F4", activeforeground="#000")
