    success_rate = round((RIGHTCLICK / TOTALCLICK) * 100, 2)
    if success_rate < 55:
        success_symbol = "×"
    elif success_rate < 80:
        success_symbol = "−"
    else:
        success_symbol = "✓"
    success_label = Label(end_frame, text=f"Başarı : %{success_rate}  {success_symbol}", background="#444", foreground="#FFF")
